import React, { useState } from 'react';

const TarotCard = ({ card }) => {
  const [imageError, setImageError] = useState(false);

  const getCardIcon = (cardName) => {
    if (cardName.includes('Wands') || cardName.includes('ไม้เท้า')) return '🔥';
    if (cardName.includes('Cups') || cardName.includes('ถ้วย')) return '💧';
    if (cardName.includes('Swords') || cardName.includes('ดาบ')) return '⚔️';
    if (cardName.includes('Pentacles') || cardName.includes('เหรียญ')) return '🪙';
    return '✨'; // For Major Arcana
  };

  const getCardColor = (cardName, isReversed) => {
    let baseColor;
    if (cardName.includes('Wands') || cardName.includes('ไม้เท้า')) 
      baseColor = 'from-orange-500 to-red-700';
    else if (cardName.includes('Cups') || cardName.includes('ถ้วย')) 
      baseColor = 'from-blue-500 to-cyan-700';
    else if (cardName.includes('Swords') || cardName.includes('ดาบ')) 
      baseColor = 'from-gray-500 to-slate-700';
    else if (cardName.includes('Pentacles') || cardName.includes('เหรียญ')) 
      baseColor = 'from-green-500 to-emerald-6700';
    else 
      baseColor = 'from-purple-600 to-pink-700'; // For Major Arcana
    
    // Add darker tone for reversed cards
    if (isReversed) {
      return baseColor + ' opacity-90';
    }
    return baseColor;
  };

  const getReversedMeaning = (originalMeaning) => {
    // Simple reversed meaning - you can expand this with specific reversed meanings
    return `(กลับหัว) ความหมายตรงข้าม หรือพลังงานที่ถูกบล็อก: ${originalMeaning}`;
  };

  const handleImageError = () => {
    setImageError(true);
  };

  return (
    <div className="max-w-md mx-auto">
      <div className={`bg-gradient-to-br ${getCardColor(card.name, card.isReversed)} rounded-xl shadow-2xl p-8 text-white transform hover:scale-105 transition-all duration-300 ${card.isReversed ? 'ring-2 ring-red-400' : ''}`}>
        <div className="text-center">
          {/* Reversed indicator */}
          {card.isReversed && (
            <div className="mb-4">
              <span className="bg-red-500 text-white px-3 py-1 rounded-full text-sm font-semibold">
                🔄 กลับหัว
              </span>
            </div>
          )}
          
          {/* Card Image */}
          <div className="mb-6">
            {card.image && !imageError ? (
              <img 
                src={card.image} 
                alt={card.nameTh}
                onError={handleImageError}
                className={`w-48 h-72 mx-auto rounded-lg shadow-lg object-cover border-2 border-white/30 transition-transform duration-300 ${
                  card.isReversed ? 'transform rotate-180' : ''
                }`}
              />
            ) : (
              <div className={`w-48 h-72 mx-auto bg-white/20 rounded-lg shadow-lg flex items-center justify-center border-2 border-white/30 transition-transform duration-300 ${
                card.isReversed ? 'transform rotate-180' : ''
              }`}>
                <div className="text-center">
                  <div className="text-6xl mb-2">
                    {getCardIcon(card.name)}
                  </div>
                  <div className="text-sm opacity-75">
                    {card.nameTh}
                  </div>
                </div>
              </div>
            )}
          </div>
          
          <h3 className="text-2xl font-bold mb-2">
            {card.nameTh} {card.isReversed ? '(กลับหัว)' : ''}
          </h3>
          
          <p className="text-lg opacity-90 mb-6">
            {card.name}
          </p>
          
          <div className="bg-white/20 backdrop-blur-sm rounded-lg p-6">
            <h4 className="text-lg font-semibold mb-3">
              🔮 ความหมาย
            </h4>
            <p className="text-base leading-relaxed">
              {card.isReversed ? getReversedMeaning(card.meaning) : card.meaning}
            </p>
          </div>
          
          <div className="mt-6 text-sm opacity-75">
            ไพ่ที่ {card.id} จาก 78 ใบ {card.isReversed ? '• กลับหัว' : '• ตั้งตรง'}
          </div>
        </div>
      </div>
    </div>
  );
};

export default TarotCard;
