// Add backward compatibility for old meaning format
const addBackwardCompatibility = (cards) => {
  return cards.map(card => ({
    ...card,
    // Add old 'meaning' property for backward compatibility
    meaning: card.meanings ? card.meanings.general : card.meaning
  }));
};

// Combine all cards
export const tarotCards = addBackwardCompatibility([...majorArcana, ...moreCards, ...minorArcana]);

// Helper functions
export const getRandomCard = () => {
  const randomIndex = Math.floor(Math.random() * tarotCards.length);
  const card = { ...tarotCards[randomIndex] };
  card.isReversed = Math.random() < 0.5;
  return card;
};

export const getRandomCards = (count) => {
  const shuffled = [...tarotCards].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, count).map(card => ({
    ...card,
    isReversed: Math.random() < 0.5
  }));
};

export const getAllCards = () => {
  return tarotCards;
};

export const getCardMeaning = (card, category = 'general') => {
  return card.meanings[category] || card.meanings.general;
};
