    if (cardName.includes('Cups') || cardName.includes('‡∏ñ‡πâ‡∏ß‡∏¢')) return 'üíß';
    if (cardName.includes('Swords') || cardName.includes('‡∏î‡∏≤‡∏ö')) return '‚öîÔ∏è';
    if (cardName.includes('Pentacles') || cardName.includes('‡πÄ‡∏´‡∏£‡∏µ‡∏¢‡∏ç')) return 'ü™ô';
    return '‚ú®'; // For Major Arcana
  };

  const getCardColor = (cardName) => {
    if (cardName.includes('Wands') || cardName.includes('‡πÑ‡∏°‡πâ‡πÄ‡∏ó‡πâ‡∏≤')) 
      return 'from-orange-500 to-red-600';
    if (cardName.includes('Cups') || cardName.includes('‡∏ñ‡πâ‡∏ß‡∏¢')) 
      return 'from-blue-500 to-cyan-600';
    if (cardName.includes('Swords') || cardName.includes('‡∏î‡∏≤‡∏ö')) 
      return 'from-gray-500 to-slate-600';
    if (cardName.includes('Pentacles') || cardName.includes('‡πÄ‡∏´‡∏£‡∏µ‡∏¢‡∏ç')) 
      return 'from-green-500 to-emerald-600';
    return 'from-purple-600 to-pink-600'; // For Major Arcana
  };

  const handleImageError = () => {
    setImageError(true);
  };

  return (
    <div className="max-w-md mx-auto">
      <div className={`bg-gradient-to-br ${getCardColor(card.name)} rounded-xl shadow-2xl p-8 text-white transform hover:scale-105 transition-all duration-300`}>
        <div className="text-center">
          {/* Card Image */}
          <div className="mb-6">
            {card.image && !imageError ? (
              <img 
                src={card.image} 
                alt={card.nameTh}
                onError={handleImageError}
                className="w-48 h-72 mx-auto rounded-lg shadow-lg object-cover border-2 border-white/30"
              />
            ) : (
              <div className="w-48 h-72 mx-auto bg-white/20 rounded-lg shadow-lg flex items-center justify-center border-2 border-white/30">
                <div className="text-center">
                  <div className="text-6xl mb-2">
                    {getCardIcon(card.name)}
                  </div>
                  <div className="text-sm opacity-75">
                    {card.nameTh}
                  </div>
                </div>
              </div>
            )}
          </div>
          
          <h3 className="text-2xl font-bold mb-2">
            {card.nameTh}
          </h3>
          
          <p className="text-lg opacity-90 mb-6">
            {card.name}
          </p>
          
          <div className="bg-white/20 backdrop-blur-sm rounded-lg p-6">
            <h4 className="text-lg font-semibold mb-3">
              üîÆ ‡∏Ñ‡∏ß‡∏≤‡∏°‡∏´‡∏°‡∏≤‡∏¢
            </h4>
            <p className="text-base leading-relaxed">
              {card.meaning}
            </p>
          </div>
          
          <div className="mt-6 text-sm opacity-75">
            ‡πÑ‡∏û‡πà‡∏ó‡∏µ‡πà {card.id} ‡∏à‡∏≤‡∏Å 78 ‡πÉ‡∏ö
          </div>
        </div>
      </div>
    </div>
  );
};

export default TarotCard;
