import React, { useState, useEffect } from "react";

const HomePage = ({ onNavigate }) => {
  // Gallery slider state
  const [currentSlide, setCurrentSlide] = useState(0);
  
  // Sample gallery images (you can replace with actual images)
  const galleryImages = [
    {
      id: 1,
      src: "/images/tarot-spread-1.jpg",
      alt: "การเปิดไพ่ทาโรต์แบบ Celtic Cross",
      title: "Celtic Cross Spread",
      description: "การเปิดไพ่แบบกางเขนเซลติก เหมาะสำหรับคำถามที่ซับซ้อน"
    },
    {
      id: 2,
      src: "/images/tarot-cards-2.jpg", 
      alt: "ไพ่ทาโรต์ Major Arcana",
      title: "Major Arcana Cards",
      description: "ไพ่ใหญ่ 22 ใบ ที่บอกเล่าเรื่องราวของชีวิต"
    },
    {
      id: 3,
      src: "/images/crystal-ball.jpg",
      alt: "ลูกแก้วคริสตัลและไพ่ทาโรต์",
      title: "Mystical Reading",
      description: "การดูดวงแบบลึกลับด้วยเครื่องมือโบราณ"
    },
    {
      id: 4,
      src: "/images/moon-tarot.jpg",
      alt: "การดูดวงยามค่ำคืน",
      title: "Night Reading",
      description: "พลังของดวงจันทร์ช่วยเสริมการดูดวง"
    }
  ];

  // Blog posts data
  const blogPosts = [
    {
      id: 1,
      title: "ความหมายของไพ่ The Fool ในชีวิตประจำวัน",
      excerpt: "ไพ่ The Fool เป็นไพ่แรกใน Major Arcana ที่สื่อถึงการเริ่มต้นใหม่ การผจญภัย และความกล้าหาญในการก้าวไปข้างหน้า...",
      date: "15 มีนาคม 2024",
      category: "ความหมายไพ่",
      image: "/images/blog-fool.jpg"
    },
    {
      id: 2,
      title: "วิธีการเตรียมตัวก่อนดูดวงไพ่ทาโรต์",
      excerpt: "การเตรียมจิตใจและสภาพแวดล้อมก่อนการดูดวงเป็นสิ่งสำคัญที่จะช่วยให้ได้ผลลัพธ์ที่แม่นยำมากขึ้น...",
      date: "12 มีนาคม 2024", 
      category: "คำแนะนำ",
      image: "/images/blog-preparation.jpg"
    },
    {
      id: 3,
      title: "ไพ่ทาโรต์กับการตัดสินใจในชีวิต",
      excerpt: "เรียนรู้วิธีใช้ไพ่ทาโรต์เป็นเครื่องมือช่วยในการตัดสินใจสำคัญ ไม่ใช่การทำนายอนาคต แต่เป็นการสะท้อนจิตใจ...",
      date: "10 มีนาคม 2024",
      category: "ปรัชญา",
      image: "/images/blog-decision.jpg"
    }
  ];

  // Auto-slide gallery
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % galleryImages.length);
    }, 5000);
    return () => clearInterval(timer);
  }, [galleryImages.length]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % galleryImages.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + galleryImages.length) % galleryImages.length);
  };

  return (
    <div className="min-h-screen bg-slate-800">
      {/* Header Section */}
      <header className="bg-slate-900 border-b border-slate-700">
        <div className="container mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            {/* Logo */}
            <div className="flex items-center space-x-3">
              <span className="text-2xl">🔮</span>
              <span className="text-xl font-bold text-white">ทาโรต์</span>
            </div>

            {/* Navigation Menu */}
            <nav className="flex space-x-1">
              <button
                onClick={() => onNavigate("home")}
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md transition-colors duration-200 text-sm font-medium"
              >
                หน้าแรก
              </button>
              <button
                onClick={() => onNavigate("daily")}
                className="text-slate-300 hover:text-white px-4 py-2 rounded-md transition-colors duration-200 text-sm font-medium"
              >
                ดูดวงรายวัน
              </button>
              <button
                onClick={() => onNavigate("monthly")}
                className="text-slate-300 hover:text-white px-4 py-2 rounded-md transition-colors duration-200 text-sm font-medium"
              >
                ดูดวงรายเดือน
              </button>
              <button
                onClick={() => onNavigate("all-cards")}
                className="text-slate-300 hover:text-white px-4 py-2 rounded-md transition-colors duration-200 text-sm font-medium"
              >
                ไพ่ทั้งหมด
              </button>
            </nav>
          </div>
        </div>
      </header>

      {/* Hero Section */}
      <main className="container mx-auto px-6 py-20">
        <div className="grid lg:grid-cols-2 gap-12 items-center">
          {/* Left Content */}
          <div className="space-y-8">
            <div className="space-y-4">
              <h1 className="text-5xl lg:text-6xl font-bold text-white leading-tight">
                ยินดีต้อนรับสู่ <span className="text-blue-500">แพลตฟอร์ม</span>
                <br />
                <span className="text-blue-500">ทาโรต์</span>
              </h1>
              <p className="text-xl text-slate-400 leading-relaxed">
                ค้นพบความลับของจักรวาลและรับคำแนะนำสำหรับชีวิตของคุณ
                ผ่านไพ่ทาโรต์โบราณที่จะช่วยนำทางในทุกด้านของชีวิต
              </p>
            </div>

            {/* Action Buttons */}
            <div className="flex flex-col sm:flex-row gap-4">
              <button
                onClick={() => onNavigate("daily")}
                className="bg-blue-600 hover:bg-blue-700 text-white px-8 py-4 rounded-lg font-semibold text-lg transition-all duration-200 transform hover:scale-105 shadow-lg"
              >
                เริ่มดูดวง
              </button>
              <button
                onClick={() => onNavigate("all-cards")}
                className="border-2 border-slate-600 hover:border-slate-500 text-white px-8 py-4 rounded-lg font-semibold text-lg transition-all duration-200 hover:bg-slate-700"
              >
                เรียนรู้เพิ่มเติม
              </button>
            </div>
          </div>

          {/* Right Content - Feature Box */}
          <div className="lg:flex justify-center">
            <div className="bg-slate-700 border-2 border-blue-500 rounded-2xl p-8 max-w-md w-full">
              <div className="space-y-6">
                <h3 className="text-2xl font-bold text-white text-center mb-6">
                  เลือกวิธีการดูดวง
                </h3>

                <div className="space-y-4">
                  <button
                    onClick={() => onNavigate("daily")}
                    className="w-full bg-gradient-to-r from-yellow-500 to-orange-500 hover:from-yellow-600 hover:to-orange-600 text-white font-semibold py-4 px-6 rounded-xl transition-all duration-200 flex items-center justify-center space-x-3 shadow-lg"
                  >
                    <span className="text-xl">🌅</span>
                    <span>ดูดวงรายวัน</span>
                  </button>

                  <button
                    onClick={() => onNavigate("monthly")}
                    className="w-full bg-gradient-to-r from-purple-500 to-blue-500 hover:from-purple-600 hover:to-blue-600 text-white font-semibold py-4 px-6 rounded-xl transition-all duration-200 flex items-center justify-center space-x-3 shadow-lg"
                  >
                    <span className="text-xl">🌙</span>
                    <span>ดูดวงรายเดือน</span>
                  </button>

                  <button
                    onClick={() => onNavigate("all-cards")}
                    className="w-full bg-gradient-to-r from-green-500 to-teal-500 hover:from-green-600 hover:to-teal-600 text-white font-semibold py-4 px-6 rounded-xl transition-all duration-200 flex items-center justify-center space-x-3 shadow-lg"
                  >
                    <span className="text-xl">📚</span>
                    <span>ความหมายไพ่ทั้งหมด</span>
                  </button>
                </div>

                <div className="text-center pt-4">
                  <p className="text-slate-400 text-sm">
                    ✨ เริ่มต้นการเดินทางของคุณวันนี้ ✨
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default HomePage;
