import React, { useState } from 'react';
import { getRandomCards } from '../data/tarotCards';
import TarotCard from './TarotCard';
import CategorySelector from './CategorySelector';
import { exportMultipleCardsToPDF } from '../utils/pdfExport';

const MonthlyReading = ({ onBack }) => {
  const [drawnCards, setDrawnCards] = useState([]);
  const [isDrawing, setIsDrawing] = useState(false);
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [showCategorySelector, setShowCategorySelector] = useState(true);

  const drawCards = () => {
    setIsDrawing(true);
    setTimeout(() => {
      const cards = getRandomCards(10);
      setDrawnCards(cards);
      setCurrentCardIndex(0);
      setIsDrawing(false);
    }, 1500);
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setShowCategorySelector(false);
  };

  const resetReading = () => {
    setDrawnCards([]);
    setCurrentCardIndex(0);
    setSelectedCategory(null);
    setShowCategorySelector(true);
  };

  const handleExportPDF = async () => {
    if (drawnCards.length > 0 && selectedCategory) {
      try {
        await exportMultipleCardsToPDF(drawnCards, selectedCategory.id);
      } catch (error) {
        console.error('Error exporting PDF:', error);
        alert('เกิดข้อผิดพลาดในการสร้างไฟล์ PDF');
      }
    }
  };

  const nextCard = () => {
    if (currentCardIndex < drawnCards.length - 1) {
      setCurrentCardIndex(currentCardIndex + 1);
    }
  };

  const prevCard = () => {
    if (currentCardIndex > 0) {
      setCurrentCardIndex(currentCardIndex - 1);
    }
  };

  // Show category selector first
  if (showCategorySelector) {
    return (
      <CategorySelector 
        onSelectCategory={handleCategorySelect}
        onBack={onBack}
      />
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-800 p-4">
      <div className="max-w-4xl mx-auto">
        <div className="flex items-center justify-between mb-8">
          <button
            onClick={onBack}
            className="bg-white/20 hover:bg-white/30 text-white px-4 py-2 rounded-lg transition-colors"
          >
            ← กลับ
          </button>
          <div className="text-center">
            <h1 className="text-3xl font-bold text-white">🌙 ดูดวงรายเดือน</h1>
            {selectedCategory && (
              <div className="mt-2 flex items-center justify-center">
                <span className="bg-white/20 backdrop-blur-sm px-4 py-2 rounded-full text-white">
                  {selectedCategory.icon} {selectedCategory.name}
                </span>
              </div>
            )}
          </div>
          <div></div>
        </div>

        <div className="text-center">
          {drawnCards.length === 0 && !isDrawing && (
            <div className="space-y-6">
              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-8">
                <h2 className="text-2xl font-semibold text-white mb-4">
                  ดูดวงสำหรับเดือนนี้
                </h2>
                <p className="text-purple-200 mb-6">
                  กดปุ่มด้านล่างเพื่อสุ่มไพ่ 10 ใบ และดูดวงรายเดือน
                </p>
                <div className="grid grid-cols-5 gap-2 max-w-md mx-auto mb-6">
                  {[...Array(10)].map((_, i) => (
                    <div key={i} className="w-16 h-24 bg-gradient-to-br from-purple-600 to-pink-600 rounded-lg shadow-lg flex items-center justify-center">
                      <span className="text-lg">🎴</span>
                    </div>
                  ))}
                </div>
              </div>
              
              <button
                onClick={drawCards}
                className="bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 text-white font-semibold py-3 px-8 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200"
              >
                🎯 สุ่มไพ่ดูดวง
              </button>
            </div>
          )}

          {isDrawing && (
            <div className="space-y-6">
              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-8">
                <h2 className="text-2xl font-semibold text-white mb-4">
                  กำลังสุ่มไพ่ 10 ใบ...
                </h2>
                <div className="grid grid-cols-5 gap-2 max-w-md mx-auto">
                  {[...Array(10)].map((_, i) => (
                    <div key={i} className="w-16 h-24 bg-gradient-to-br from-purple-600 to-pink-600 rounded-lg shadow-lg flex items-center justify-center animate-pulse">
                      <span className="text-lg animate-spin">🎴</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}

          {drawnCards.length > 0 && (
            <div className="space-y-6">
              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-8">
                <div className="flex items-center justify-between mb-6">
                  <h2 className="text-2xl font-semibold text-white">
                    ไพ่ที่ {currentCardIndex + 1} จาก 10 ใบ
                  </h2>
                  <div className="text-purple-200">
                    {currentCardIndex + 1}/10
                  </div>
                </div>
                
                <TarotCard card={drawnCards[currentCardIndex]} category={selectedCategory?.id} />
                
                <div className="flex justify-center space-x-4 mt-6">
                  <button
                    onClick={prevCard}
                    disabled={currentCardIndex === 0}
                    className="bg-white/20 hover:bg-white/30 disabled:opacity-50 disabled:cursor-not-allowed text-white px-6 py-2 rounded-lg transition-colors"
                  >
                    ← ไพ่ก่อนหน้า
                  </button>
                  <button
                    onClick={nextCard}
                    disabled={currentCardIndex === drawnCards.length - 1}
                    className="bg-white/20 hover:bg-white/30 disabled:opacity-50 disabled:cursor-not-allowed text-white px-6 py-2 rounded-lg transition-colors"
                  >
                    ไพ่ถัดไป →
                  </button>
                </div>
                
                <div className="flex justify-center mt-4">
                  <div className="flex space-x-1">
                    {drawnCards.map((_, index) => (
                      <button
                        key={index}
                        onClick={() => setCurrentCardIndex(index)}
                        className={`w-3 h-3 rounded-full transition-colors ${
                          index === currentCardIndex 
                            ? 'bg-yellow-400' 
                            : 'bg-white/30 hover:bg-white/50'
                        }`}
                      />
                    ))}
                  </div>
                </div>
              </div>
              
              <div className="flex justify-center space-x-4">
                <button
                  onClick={handleExportPDF}
                  className="bg-gradient-to-r from-purple-500 to-indigo-600 hover:from-purple-600 hover:to-indigo-700 text-white font-semibold py-3 px-8 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200"
                >
                  📄 Export PDF
                </button>
                <button
                  onClick={resetReading}
                  className="bg-gradient-to-r from-green-500 to-teal-600 hover:from-green-600 hover:to-teal-700 text-white font-semibold py-3 px-8 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200"
                >
                  🔄 สุ่มไพ่ใหม่
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MonthlyReading;
