import React, { useState } from 'react';
import { getAllCards } from '../data/tarotCards';
import TarotCard from './TarotCard';

const MonthlyReading = ({ onBack }) => {
  const [selectedCards, setSelectedCards] = useState([]);
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [showReading, setShowReading] = useState(false);
  const [shuffledCards, setShuffledCards] = useState([]);
  const [isShuffling, setIsShuffling] = useState(false);
  const allCards = getAllCards();

  const shuffleCards = () => {
    setIsShuffling(true);
    setTimeout(() => {
      const shuffled = [...allCards].sort(() => Math.random() - 0.5);
      setShuffledCards(shuffled);
      setIsShuffling(false);
    }, 1500);
  };

  const handleCardSelect = (cardIndex) => {
    const card = shuffledCards[cardIndex];
    if (selectedCards.find(c => c.id === card.id)) {
      // Remove card if already selected
      setSelectedCards(selectedCards.filter(c => c.id !== card.id));
    } else if (selectedCards.length < 10) {
      // Add card if less than 10 selected
      setSelectedCards([...selectedCards, card]);
    }
  };

  const startReading = () => {
    if (selectedCards.length === 10) {
      setShowReading(true);
      setCurrentCardIndex(0);
    }
  };

  const resetReading = () => {
    setSelectedCards([]);
    setShowReading(false);
    setCurrentCardIndex(0);
    setShuffledCards([]);
  };

  const nextCard = () => {
    if (currentCardIndex < selectedCards.length - 1) {
      setCurrentCardIndex(currentCardIndex + 1);
    }
  };

  const prevCard = () => {
    if (currentCardIndex > 0) {
      setCurrentCardIndex(currentCardIndex - 1);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-800 p-4">
      <div className="max-w-6xl mx-auto">
        <div className="flex items-center justify-between mb-8">
          <button
            onClick={onBack}
            className="bg-white/20 hover:bg-white/30 text-white px-4 py-2 rounded-lg transition-colors"
          >
            ← กลับ
          </button>
          <h1 className="text-3xl font-bold text-white">🌙 ดูดวงรายเดือน</h1>
          <div></div>
        </div>

        <div className="text-center">
          {!showReading && (
            <div className="space-y-6">
              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-6">
                <h2 className="text-2xl font-semibold text-white mb-4">
                  เลือกไพ่ 10 ใบสำหรับดูดวงรายเดือน
                </h2>
                <p className="text-purple-200 mb-4">
                  คลิกเลือกไพ่ 10 ใบจากไพ่ทั้งหมด 78 ใบ ({selectedCards.length}/10)
                </p>
                
                {/* Selected cards preview */}
                {selectedCards.length > 0 && (
                  <div className="mb-6">
                    <h3 className="text-lg font-semibold text-white mb-3">ไพ่ที่เลือกแล้ว:</h3>
                    <div className="flex flex-wrap justify-center gap-2 mb-4">
                      {selectedCards.map((card, index) => (
                        <div key={card.id} className="bg-yellow-500 text-white px-3 py-1 rounded-full text-sm">
                          {index + 1}. {card.nameTh}
                        </div>
                      ))}
                    </div>
                    {selectedCards.length === 10 && (
                      <button
                        onClick={startReading}
                        className="bg-gradient-to-r from-green-500 to-teal-600 hover:from-green-600 hover:to-teal-700 text-white font-semibold py-3 px-8 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200 mb-6"
                      >
                        🔮 เริ่มดูดวง
                      </button>
                    )}
                  </div>
                )}
              </div>

              {/* All cards selection */}
              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-6">
                <div className="grid grid-cols-3 sm:grid-cols-4 md:grid-cols-6 lg:grid-cols-8 gap-3">
                  {allCards.map((card) => {
                    const isSelected = selectedCards.find(c => c.id === card.id);
                    const canSelect = selectedCards.length < 10 || isSelected;
                    
                    return (
                      <button
                        key={card.id}
                        onClick={() => handleCardSelect(card)}
                        disabled={!canSelect}
                        className={`p-3 rounded-lg shadow-lg transform transition-all duration-200 text-center relative ${
                          isSelected 
                            ? 'bg-gradient-to-br from-yellow-400 to-orange-500 scale-105 ring-2 ring-yellow-300' 
                            : canSelect
                              ? 'bg-gradient-to-br from-purple-600 to-pink-600 hover:from-purple-500 hover:to-pink-500 hover:scale-105'
                              : 'bg-gray-500 opacity-50 cursor-not-allowed'
                        } text-white`}
                      >
                        <div className="text-lg mb-1">🎴</div>
                        <div className="text-xs font-medium">{card.nameTh}</div>
                        {isSelected && (
                          <div className="absolute -top-2 -right-2 bg-yellow-400 text-black rounded-full w-6 h-6 flex items-center justify-center text-xs font-bold">
                            {selectedCards.findIndex(c => c.id === card.id) + 1}
                          </div>
                        )}
                      </button>
                    );
                  })}
                </div>
              </div>
              
              {selectedCards.length > 0 && (
                <button
                  onClick={resetReading}
                  className="bg-gradient-to-r from-red-500 to-pink-600 hover:from-red-600 hover:to-pink-700 text-white font-semibold py-2 px-6 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200"
                >
                  🔄 เริ่มใหม่
                </button>
              )}
            </div>
          )}

          {showReading && (
            <div className="space-y-6">
              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-8">
                <div className="flex items-center justify-between mb-6">
                  <h2 className="text-2xl font-semibold text-white">
                    ไพ่ที่ {currentCardIndex + 1} จาก 10 ใบ
                  </h2>
                  <div className="text-purple-200">
                    {currentCardIndex + 1}/10
                  </div>
                </div>
                
                <TarotCard card={selectedCards[currentCardIndex]} />
                
                <div className="flex justify-center space-x-4 mt-6">
                  <button
                    onClick={prevCard}
                    disabled={currentCardIndex === 0}
                    className="bg-white/20 hover:bg-white/30 disabled:opacity-50 disabled:cursor-not-allowed text-white px-6 py-2 rounded-lg transition-colors"
                  >
                    ← ไพ่ก่อนหน้า
                  </button>
                  <button
                    onClick={nextCard}
                    disabled={currentCardIndex === selectedCards.length - 1}
                    className="bg-white/20 hover:bg-white/30 disabled:opacity-50 disabled:cursor-not-allowed text-white px-6 py-2 rounded-lg transition-colors"
                  >
                    ไพ่ถัดไป →
                  </button>
                </div>
                
                <div className="flex justify-center mt-4">
                  <div className="flex space-x-1">
                    {selectedCards.map((_, index) => (
                      <button
                        key={index}
                        onClick={() => setCurrentCardIndex(index)}
                        className={`w-3 h-3 rounded-full transition-colors ${
                          index === currentCardIndex 
                            ? 'bg-yellow-400' 
                            : 'bg-white/30 hover:bg-white/50'
                        }`}
                      />
                    ))}
                  </div>
                </div>
              </div>
              
              <button
                onClick={resetReading}
                className="bg-gradient-to-r from-green-500 to-teal-600 hover:from-green-600 hover:to-teal-700 text-white font-semibold py-3 px-8 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200"
              >
                🔄 เลือกไพ่ใหม่
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MonthlyReading;
