import React, { useState } from "react";
import { getRandomCard } from "../data/tarotCards";
import TarotCard from "./TarotCard";
import CategorySelector from "./CategorySelector";

const DailyReading = ({ onBack }) => {
  const [drawnCard, setDrawnCard] = useState(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [showCategorySelector, setShowCategorySelector] = useState(true);

  const drawCard = () => {
    setIsDrawing(true);
    setTimeout(() => {
      const card = getRandomCard();
      setDrawnCard(card);
      setIsDrawing(false);
    }, 1000);
  };

  const resetReading = () => {
    setDrawnCard(null);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-800 p-4">
      <div className="max-w-2xl mx-auto">
        <div className="flex items-center justify-between mb-8">
          <button
            onClick={onBack}
            className="bg-white/20 hover:bg-white/30 text-white px-4 py-2 rounded-lg transition-colors"
          >
            ← กลับ
          </button>
          <h1 className="text-3xl font-bold text-white">🌅 ดูดวงรายวัน</h1>
          <div></div>
        </div>

        <div className="text-center">
          {!drawnCard && !isDrawing && (
            <div className="space-y-6">
              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-8">
                <h2 className="text-2xl font-semibold text-white mb-4">
                  เตรียมพร้อมสำหรับวันใหม่
                </h2>
                <p className="text-purple-200 mb-6">
                  กดปุ่มด้านล่างเพื่อสุ่มไพ่ 1 ใบ และดูดวงสำหรับวันนี้
                </p>
                <div className="w-32 h-48 mx-auto bg-gradient-to-br from-purple-600 to-pink-600 rounded-xl shadow-lg flex items-center justify-center mb-6">
                  <span className="text-4xl">🎴</span>
                </div>
              </div>
              
              <button
                onClick={drawCard}
                className="bg-gradient-to-r from-yellow-400 to-orange-500 hover:from-yellow-500 hover:to-orange-600 text-white font-semibold py-3 px-8 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200"
              >
                🎯 สุ่มไพ่ดูดวง
              </button>
            </div>
          )}

          {isDrawing && (
            <div className="space-y-6">
              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-8">
                <h2 className="text-2xl font-semibold text-white mb-4">
                  กำลังสุ่มไพ่...
                </h2>
                <div className="w-32 h-48 mx-auto bg-gradient-to-br from-purple-600 to-pink-600 rounded-xl shadow-lg flex items-center justify-center animate-pulse">
                  <span className="text-4xl animate-spin">🎴</span>
                </div>
              </div>
            </div>
          )}

          {drawnCard && (
            <div className="space-y-6">
              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-8">
                <h2 className="text-2xl font-semibold text-white mb-6">
                  ไพ่ของคุณวันนี้
                </h2>
                <TarotCard card={drawnCard} />
              </div>
              
              <button
                onClick={resetReading}
                className="bg-gradient-to-r from-green-500 to-teal-600 hover:from-green-600 hover:to-teal-700 text-white font-semibold py-3 px-8 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200"
              >
                🔄 สุ่มไพ่ใหม่
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DailyReading;
