export const getRandomCard = () => {
  const randomIndex = Math.floor(Math.random() * tarotCards.length);
  const card = { ...tarotCards[randomIndex] };
  card.isReversed = Math.random() < 0.5;
  return card;
};

export const getRandomCards = (count) => {
  const shuffled = [...tarotCards].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, count).map((card) => ({
    ...card,
    isReversed: Math.random() < 0.5,
  }));
};

export const getAllCards = () => {
  return tarotCards;
};

// Function to get category-specific meaning
export const getCardMeaning = (card, category = "general") => {
  if (category === "general" || !category) {
    return card.meaning;
  }

  const cardName = card.nameTh;

  switch (category) {
    case "love":
      if (cardName.includes("คนโง่"))
        return "ความรักใหม่กำลังจะเข้ามา การเริ่มต้นความสัมพันธ์ใหม่ หรือการเปิดใจให้กับความรักอีกครั้ง";
      if (cardName.includes("นักมายากล"))
        return "คุณมีเสน่ห์และพลังในการดึงดูดคนที่ใช่ ใช้ความสามารถในการสื่อสารเพื่อสร้างความสัมพันธ์ที่แข็งแกร่ง";
      if (cardName.includes("จักรพรรดินี"))
        return "ความรักที่อบอุ่นและเต็มไปด้วยความสุข อาจมีข่าวดีเรื่องการแต่งงานหรือการมีลูก";
      if (cardName.includes("คู่รัก"))
        return "ความรักแท้และความสัมพันธ์ที่สมบูรณ์แบบ อาจมีการตัดสินใจสำคัญเกี่ยวกับความรัก";
      return `ความหมายด้านความรักของ${cardName}: ความรักและความสัมพันธ์จะมีการเปลี่ยนแปลงในทางที่ดี`;

