import React, { useState } from 'react';
import { getAllCards } from '../data/tarotCards';
import TarotCard from './TarotCard';

const DailyReading = ({ onBack }) => {
  const [selectedCard, setSelectedCard] = useState(null);
  const [showReading, setShowReading] = useState(false);
  const [shuffledCards, setShuffledCards] = useState([]);
  const [isShuffling, setIsShuffling] = useState(false);
  const [isPredicting, setIsPredicting] = useState(false);
  const allCards = getAllCards();

  const shuffleCards = () => {
    setIsShuffling(true);
    setTimeout(() => {
      const shuffled = [...allCards].sort(() => Math.random() - 0.5).map(card => ({
        ...card,
        // 50% chance for each card to be reversed
        isReversed: Math.random() < 0.5
      }));
      setShuffledCards(shuffled);
      setIsShuffling(false);
    }, 1500);
  };

  const handleCardSelect = (cardIndex) => {
    const card = shuffledCards[cardIndex];
    setSelectedCard(card);
  };

  const startReading = () => {
    if (selectedCard) {
      setIsPredicting(true);
      setTimeout(() => {
        setIsPredicting(false);
        setShowReading(true);
      }, 3000); // 3 seconds loading
    }
  };

  const resetReading = () => {
    setSelectedCard(null);
    setShowReading(false);
    setShuffledCards([]);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-800 p-4">
      <div className="max-w-2xl mx-auto">
        <div className="flex items-center justify-between mb-8">
          <button
            onClick={onBack}
            className="bg-white/20 hover:bg-white/30 text-white px-4 py-2 rounded-lg transition-colors"
          >
            ← กลับ
          </button>
          <h1 className="text-3xl font-bold text-white">🌅 ดูดวงรายวัน</h1>
          <div></div>
        </div>

        <div className="text-center">
          {!showReading && (
            <div className="space-y-6">
              {/* Initial state - before shuffling */}
              {shuffledCards.length === 0 && !isShuffling && (
                <div className="bg-white/10 backdrop-blur-sm rounded-xl p-8">
                  <h2 className="text-2xl font-semibold text-white mb-4">
                    เตรียมพร้อมสำหรับวันใหม่
                  </h2>
                  <p className="text-purple-200 mb-6">
                    กดปุ่มด้านล่างเพื่อสับไพ่และเลือกไพ่ 1 ใบสำหรับวันนี้
                  </p>
                  <div className="w-32 h-48 mx-auto bg-gradient-to-br from-purple-600 to-pink-600 rounded-xl shadow-lg flex items-center justify-center mb-6">
                    <span className="text-4xl">🎴</span>
                  </div>
                  <button
                    onClick={shuffleCards}
                    className="bg-gradient-to-r from-yellow-400 to-orange-500 hover:from-yellow-500 hover:to-orange-600 text-white font-semibold py-3 px-8 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200"
                  >
                    🔀 สับไพ่
                  </button>
                </div>
              )}

              {/* Shuffling animation */}
              {isShuffling && (
                <div className="bg-white/10 backdrop-blur-sm rounded-xl p-8">
                  <h2 className="text-2xl font-semibold text-white mb-4">
                    กำลังสับไพ่...
                  </h2>
                  <div className="grid grid-cols-6 gap-2 max-w-lg mx-auto">
                    {[...Array(12)].map((_, i) => (
                      <div key={i} className="w-12 h-18 bg-gradient-to-br from-purple-600 to-pink-600 rounded-lg shadow-lg flex items-center justify-center animate-pulse">
                        <span className="text-sm animate-spin">🎴</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Card selection after shuffling */}
              {shuffledCards.length > 0 && !isShuffling && (
                <>
                  <div className="bg-white/10 backdrop-blur-sm rounded-xl p-6">
                    <h2 className="text-2xl font-semibold text-white mb-4">
                      เลือกไพ่ 1 ใบสำหรับดูดวงรายวัน
                    </h2>
                    <p className="text-purple-200 mb-4">
                      คลิกเลือกไพ่ 1 ใบจากไพ่ที่สับแล้ว
                    </p>
                    
                    {/* Selected card preview */}
                    {selectedCard && (
                      <div className="mb-6">
                        <h3 className="text-lg font-semibold text-white mb-3">ไพ่ที่เลือกแล้ว:</h3>
                        <div className="flex justify-center mb-4">
                          <div className="bg-yellow-500 text-white px-4 py-2 rounded-full text-sm">
                            ไพ่ของคุณวันนี้
                          </div>
                        </div>
                        <button
                          onClick={startReading}
                          className="bg-gradient-to-r from-green-500 to-teal-600 hover:from-green-600 hover:to-teal-700 text-white font-semibold py-3 px-8 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200 mb-6"
                        >
                          🔮 เริ่มดูดวง
                        </button>
                      </div>
                    )}
                  </div>

                  {/* All cards selection - show only card backs */}
                  <div className="bg-white/10 backdrop-blur-sm rounded-xl p-6">
                    <div className="grid grid-cols-3 sm:grid-cols-4 md:grid-cols-6 lg:grid-cols-8 gap-3">
                      {shuffledCards.map((card, index) => {
                        const isSelected = selectedCard && selectedCard.id === card.id;
                        
                        return (
                          <button
                            key={`${card.id}-${index}`}
                            onClick={() => handleCardSelect(index)}
                            className={`p-3 rounded-lg shadow-lg transform transition-all duration-200 text-center relative ${
                              isSelected 
                                ? 'bg-gradient-to-br from-yellow-400 to-orange-500 scale-105 ring-2 ring-yellow-300' 
                                : 'bg-gradient-to-br from-purple-600 to-pink-600 hover:from-purple-500 hover:to-pink-500 hover:scale-105'
                            } text-white`}
                          >
                            <div className="text-lg mb-1">🎴</div>
                            <div className="text-xs font-medium">หลังไพ่</div>
                            {isSelected && (
                              <div className="absolute -top-2 -right-2 bg-yellow-400 text-black rounded-full w-6 h-6 flex items-center justify-center text-xs font-bold">
                                ✓
                              </div>
                            )}
                          </button>
                        );
                      })}
                    </div>
                  </div>
                  
                  <div className="flex justify-center space-x-4">
                    <button
                      onClick={shuffleCards}
                      className="bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 text-white font-semibold py-2 px-6 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200"
                    >
                      🔀 สับไพ่ใหม่
                    </button>
                    {selectedCard && (
                      <button
                        onClick={resetReading}
                        className="bg-gradient-to-r from-red-500 to-pink-600 hover:from-red-600 hover:to-pink-700 text-white font-semibold py-2 px-6 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200"
                      >
                        🔄 เริ่มใหม่
                      </button>
                    )}
                  </div>
                </>
              )}
            </div>
          )}

          {showReading && selectedCard && (
            <div className="space-y-6">
              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-8">
                <h2 className="text-2xl font-semibold text-white mb-6">
                  ไพ่ของคุณวันนี้
                </h2>
                <TarotCard card={selectedCard} />
              </div>
              
              <button
                onClick={resetReading}
                className="bg-gradient-to-r from-green-500 to-teal-600 hover:from-green-600 hover:to-teal-700 text-white font-semibold py-3 px-8 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200"
              >
                🔄 เลือกไพ่ใหม่
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DailyReading;
