import React, { useState } from 'react';
import { getAllCards } from '../data/tarotCards';
import TarotCard from './TarotCard';
import { exportAllCardsToPDF } from '../utils/pdfExport';

const AllCards = ({ onBack }) => {
  const [selectedCard, setSelectedCard] = useState(null);
  const allCards = getAllCards();

  const handleExportAllCardsPDF = async () => {
    try {
      await exportAllCardsToPDF();
    } catch (error) {
      console.error('Error exporting PDF:', error);
      alert('เกิดข้อผิดพลาดในการสร้างไฟล์ PDF');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-800 p-4">
      <div className="max-w-6xl mx-auto">
        <div className="flex items-center justify-between mb-8">
          <button
            onClick={onBack}
            className="bg-white/20 hover:bg-white/30 text-white px-4 py-2 rounded-lg transition-colors"
          >
            ← กลับ
          </button>
          <h1 className="text-3xl font-bold text-white">📚 ความหมายไพ่ทั้งหมด</h1>
          <div></div>
        </div>

        {!selectedCard ? (
          <div className="space-y-8">
            <div className="text-center">
              <p className="text-purple-200 text-lg mb-4">
                เลือกไพ่ที่คุณต้องการดูความหมาย (ทั้งหมด 78 ใบ)
              </p>
              <button
                onClick={handleExportAllCardsPDF}
                className="bg-gradient-to-r from-purple-500 to-indigo-600 hover:from-purple-600 hover:to-indigo-700 text-white font-semibold py-3 px-8 rounded-xl shadow-lg transform hover:scale-105 transition-all duration-200 mb-8"
              >
                📄 Export ความหมายไพ่ทั้งหมดเป็น PDF
              </button>
            </div>

            {/* Major Arcana */}
            <div className="bg-white/10 backdrop-blur-sm rounded-xl p-6">
              <h2 className="text-2xl font-semibold text-white mb-6 text-center">
                🌟 Major Arcana (22 ใบ)
              </h2>
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4">
                {allCards.slice(0, 22).map((card) => (
                  <button
                    key={card.id}
                    onClick={() => setSelectedCard(card)}
                    className="bg-gradient-to-br from-purple-600 to-pink-600 hover:from-purple-500 hover:to-pink-500 text-white p-4 rounded-lg shadow-lg transform hover:scale-105 transition-all duration-200 text-center"
                  >
                    <div className="text-2xl mb-2">🎴</div>
                    <div className="text-sm font-medium">{card.nameTh}</div>
                    <div className="text-xs opacity-75">{card.name}</div>
                  </button>
                ))}
              </div>
            </div>

            {/* Minor Arcana */}
            <div className="bg-white/10 backdrop-blur-sm rounded-xl p-6">
              <h2 className="text-2xl font-semibold text-white mb-6 text-center">
                ⚡ Minor Arcana (56 ใบ)
              </h2>
              
              {/* Wands */}
              <div className="mb-8">
                <h3 className="text-lg font-semibold text-yellow-300 mb-4">🔥 Wands (ไม้เท้า)</h3>
                <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-7 gap-3">
                  {allCards.slice(22, 36).map((card) => (
                    <button
                      key={card.id}
                      onClick={() => setSelectedCard(card)}
                      className="bg-gradient-to-br from-orange-500 to-red-600 hover:from-orange-400 hover:to-red-500 text-white p-3 rounded-lg shadow-lg transform hover:scale-105 transition-all duration-200 text-center"
                    >
                      <div className="text-lg mb-1">🔥</div>
                      <div className="text-xs font-medium">{card.nameTh}</div>
                    </button>
                  ))}
                </div>
              </div>

              {/* Cups */}
              <div className="mb-8">
                <h3 className="text-lg font-semibold text-blue-300 mb-4">💧 Cups (ถ้วย)</h3>
                <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-7 gap-3">
                  {allCards.slice(36, 50).map((card) => (
                    <button
                      key={card.id}
                      onClick={() => setSelectedCard(card)}
                      className="bg-gradient-to-br from-blue-500 to-cyan-600 hover:from-blue-400 hover:to-cyan-500 text-white p-3 rounded-lg shadow-lg transform hover:scale-105 transition-all duration-200 text-center"
                    >
                      <div className="text-lg mb-1">💧</div>
                      <div className="text-xs font-medium">{card.nameTh}</div>
                    </button>
                  ))}
                </div>
              </div>

              {/* Swords */}
              <div className="mb-8">
                <h3 className="text-lg font-semibold text-gray-300 mb-4">⚔️ Swords (ดาบ)</h3>
                <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-7 gap-3">
                  {allCards.slice(50, 64).map((card) => (
                    <button
                      key={card.id}
                      onClick={() => setSelectedCard(card)}
                      className="bg-gradient-to-br from-gray-500 to-slate-600 hover:from-gray-400 hover:to-slate-500 text-white p-3 rounded-lg shadow-lg transform hover:scale-105 transition-all duration-200 text-center"
                    >
                      <div className="text-lg mb-1">⚔️</div>
                      <div className="text-xs font-medium">{card.nameTh}</div>
                    </button>
                  ))}
                </div>
              </div>

              {/* Pentacles */}
              <div>
                <h3 className="text-lg font-semibold text-green-300 mb-4">🪙 Pentacles (เหรียญ)</h3>
                <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-7 gap-3">
                  {allCards.slice(64, 78).map((card) => (
                    <button
                      key={card.id}
                      onClick={() => setSelectedCard(card)}
                      className="bg-gradient-to-br from-green-500 to-emerald-600 hover:from-green-400 hover:to-emerald-500 text-white p-3 rounded-lg shadow-lg transform hover:scale-105 transition-all duration-200 text-center"
                    >
                      <div className="text-lg mb-1">🪙</div>
                      <div className="text-xs font-medium">{card.nameTh}</div>
                    </button>
                  ))}
                </div>
              </div>
            </div>
          </div>
        ) : (
          <div className="max-w-2xl mx-auto">
            <div className="bg-white/10 backdrop-blur-sm rounded-xl p-8">
              <button
                onClick={() => setSelectedCard(null)}
                className="mb-6 bg-white/20 hover:bg-white/30 text-white px-4 py-2 rounded-lg transition-colors"
              >
                ← กลับไปดูไพ่ทั้งหมด
              </button>
              <TarotCard card={selectedCard} />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AllCards;
